/******************************************************************************************
 * 특정 요소 ID 하위 엘리먼트를 Json 객체로 리턴 @ Cracky
 * 
 * var jsn = getJsonParam('특정 요소 ID');
 * 엘리먼트 [name] 속성 기준 
 * 
 * @null element 제외 추가  2021.01.08
 ******************************************************************************************/

jQuery.extend({getJsonParam:function(_id){var obj=jQuery('#'+_id+' :input');var params={};jQuery.each(obj,function(i,o){var nam=o.name==null||o.name==''?o.id:o.name;if(nam!=null&&$.trim(nam)!=''&&nam!=undefined){if(!params.hasOwnProperty(nam)){if(o.type=='radio'){var rval=jQuery('#'+_id+' :input:radio[name='+nam+']:checked').val();params[nam]=rval==undefined?"":rval}else if(o.type=='checkbox'){var innerArray=[];var innerCnt=0;jQuery.each(jQuery('#'+_id+' :input:checkbox[name='+nam+']:checked'),function(c,k){innerArray[innerCnt]=k.value;innerCnt++});params[nam]=innerArray}else if(o.type=='textarea'){params[nam]=o.value}else{var inpt=jQuery('#'+_id+' :input[name='+nam+']');if(inpt!=null&&inpt!=undefined&&inpt.length>0){if(inpt.length==1)params[nam]=o.value;else{var innerArray=[];var innerCnt=0;jQuery.each(inpt,function(c,k){innerArray[innerCnt]=k.value;innerCnt++});params[nam]=innerArray}}}}}});return params},bindCursor:function(o){if(o==null||o==undefined||o.attr==null||o.attr==undefined){alert('jQuery.bindCursor({ attr : "bind input type")}')}else{jQuery(o.attr).bind('keydown',function(){if(event.keyCode=='13'){var currentOdr=this.odr;var minVal=0;var minObj=null;var flg=false;jQuery.each(jQuery('input'),function(i,o){if((parseInt(currentOdr)+1)==o.odr){jQuery('#'+o.id).focus();flg=true;return false}else if((parseInt(currentOdr)+1)<o.odr){if(minVal==0)minVal=o.odr;if(minVal>o.odr){minVal=o.odr;minObj=o}}else{if(o.odr=='1'){jQuery('#'+o.id).focus()}}});if(!flg&&minVal>0&&minObj!=null){jQuery('#'+minObj.id).focus();return false}return false}})}}});